<script setup>
import { ref, onMounted, onBeforeUnmount } from 'vue'
import gsap from 'gsap'
import ScrollTrigger from 'gsap/ScrollTrigger'

gsap.registerPlugin(ScrollTrigger)

const timelineRefs = ref([])
const visibleItems = ref([])

const timelineItems = ref([
	{
		time: '4:00 PM',
		content: 'Check-in begins. Free parking available on premises. Western toilet facilities provided for guest convenience.'
	},
	{
		time: '4:30 PM',
		content: 'Complimentary snacks served. Access to free activities including badminton, football, volleyball, cricket equipment available.'
	},
	{
		time: '6:00 PM',
		content: 'Music system starts playing. Evening entertainment begins with curated playlist.'
	},
	{
		time: '7:00 PM',
		content: 'BBQ session starts with both vegetarian and non-vegetarian options. Limited servings per guest.'
	},
	{
		time: '8:30 PM',
		content: 'BBQ session ends. Break time before dinner service.'
	},
	{
		time: '9:30 PM',
		content: 'Dinner service begins. Unlimited buffet including veg options (paneer masala, dal, roti, rice, salad, sweet dish, aloo sabji/matki) and non-veg options (chicken curry, egg masala, roti, rice, salad, sweet dish).'
	},
	{
		time: '11:00 PM',
		content: 'Dinner service ends. Music system continues until 10 PM.'
	},
	{
		time: '7:00 AM',
		content: 'Next morning breakfast service begins. Poha and tea served.'
	},
	{
		time: '11:00 AM',
		content: 'Check-out time. Please ensure rooms are vacated by this time.'
	}
])

// Initialize visibleItems array
visibleItems.value = new Array(timelineItems.value.length).fill(false)

const initPanelScroll = () => {
	const panels = gsap.utils.toArray('.panel')

	// Create ScrollTriggers for panel tops
	const tops = panels.map(panel =>
		ScrollTrigger.create({
			trigger: panel,
			start: "top top"
		})
	)

	// Pin panels
	panels.forEach((panel, i) => {
		ScrollTrigger.create({
			trigger: panel,
			start: () => panel.offsetHeight < window.innerHeight ? "top top" : "bottom bottom",
			pin: true,
			pinSpacing: false
		})
	})

	// Create snap effect
	// ScrollTrigger.create({
	// 	snap: {
	// 		snapTo: (progress, self) => {
	// 			const panelStarts = tops.map(st => st.start)
	// 			const snapScroll = gsap.utils.snap(panelStarts, self.scroll())
	// 			return gsap.utils.normalize(0, ScrollTrigger.maxScroll(window), snapScroll)
	// 		},
	// 		duration: 10000000
	// 	}
	// })
}

const isItemInView = (element) => {
	if (!element) return false
	const rect = element.getBoundingClientRect()
	return (
		rect.top >= 0 &&
		rect.left >= 0 &&
		rect.bottom <= (window.innerHeight || document.documentElement.clientHeight) &&
		rect.right <= (window.innerWidth || document.documentElement.clientWidth)
	)
}

const checkVisibility = () => {
	timelineRefs.value.forEach((item, index) => {
		if (isItemInView(item)) {
			visibleItems.value[index] = true
		}
	})
}

onMounted(() => {
	// Initialize panel scroll effect
	initPanelScroll()

	// Add event listeners for timeline visibility
	window.addEventListener('load', checkVisibility)
	window.addEventListener('resize', checkVisibility)
	window.addEventListener('scroll', checkVisibility)

	checkVisibility()
})

onBeforeUnmount(() => {
	window.removeEventListener('load', checkVisibility)
	window.removeEventListener('resize', checkVisibility)
	window.removeEventListener('scroll', checkVisibility)

	// Kill all ScrollTrigger instances
	ScrollTrigger.getAll().forEach(trigger => trigger.kill())
})
</script>
